/**
 * @memberof Unpleasant
 * @desc
 * Generic 2 Noise
 * @example
 * import { generic2Noise } from 'unpleasant';
 * const noise = generic2Noise(Math.random(), Math.random());
 * @see https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
 * @param {!Number} x
 * @param {!Number} y
 * @returns {Number}
 */
const generic2 = (x, y) => {
  const ix = floor(x);
  const iy = floor(y);

  const ux = smooth(x % 1);
  const uy = smooth(y % 1);

  const a = mix(rand(ix, iy), rand(ix + 1.0, iy), ux);
  const b = mix(rand(ix, iy + 1.0), rand(ix + 1.0, iy + 1.0), ux);

  const res = mix(a, b, uy);
  return res * res;
};
export default generic2;

